csharp_specflow.template:
[Binding]
public class StepDefinitions
{
    private ScenarioContext _scenarioContext;
    private Cart _cart;

    public StepDefinitions(ScenarioContext scenarioContext)
    {
        _scenarioContext = scenarioContext;
        _cart = new Cart();
    }

    [Given(@"the user has (\d+) items in the cart")]
    public void GivenUserHasItemsInCart(int count)
    {
        _cart.AddItems(count);
    }

    [When(@"the user applies a discount of (\d+)%")]
    public void WhenApplyDiscount(int percentage)
    {
        _cart.ApplyDiscount(percentage);
    }

    [Then(@"the total should be (\d+\.?\d*)")]
    public void ThenTotalShould(decimal expectedTotal)
    {
        Assert.Equal(expectedTotal, _cart.GetTotal());
    }
}

python_behave.template:
from behave import given, when, then

@given('the user has {count:d} items in the cart')
def step_user_has_items(context, count):
    context.cart = Cart()
    context.cart.add_items(count)

@when('the user applies a discount of {discount:d}%')
def step_apply_discount(context, discount):
    context.cart.apply_discount(discount)

@then('the total should be {total:f}')
def step_verify_total(context, total):
    assert context.cart.get_total() == total, \
        f"Expected {total}, got {context.cart.get_total()}"
